import React from "react";
import { Grid, Container } from "@material-ui/core";
import News from "../images/news.jpg";

function Main() {
  return (
    <div>
      <h1 style={{ textAlign: "center" }}>Our Latest News</h1>
      <Grid container spacing={3}>
        <Grid item sm={6} xs={12}>
          <p>Dublin-based Developer of Voice Tech for Kids Raises $6.5M</p>
          <p>
            DUBLIN, Ireland (April 21st, 2020) — SoapBox Labs, pioneering
            developer of safe and accurate voice technology for kids, today
            announced new funding of $6.5M. “SoapBox is at the nexus of some big
            trends right now – remote learning, voice, kidtech and data privacy.
            We’ve invested deeply over the last 7 years in our people and our
            technology. With this funding we’re poised to capitalize on our
            strengths and the global market opportunities opening up to us in
            literacy, language learning and toys” says Dr. Patricia Scanlon,
            Founder and CEO.
          </p>
        </Grid>
        <Grid item sm={6} xs={12}>
          <img src={News} alt="Logo" />
        </Grid>
      </Grid>
    </div>
  );
}

export default Main;
